import React, { useState } from 'react';
import { Container, Typography, Button, Select, MenuItem, FormControl, InputLabel, Grid, Paper } from '@mui/material';
import { useDropzone } from 'react-dropzone';

const App = () => {
  const [dataset, setDataset] = useState(null);
  const [algorithm, setAlgorithm] = useState('');

  const handleDrop = (acceptedFiles) => {
    if (acceptedFiles.length > 0) {
      setDataset(acceptedFiles[0]);
    }
  };

  const { getRootProps, getInputProps } = useDropzone({ onDrop: handleDrop });

  const runAlgorithm = () => {
    if (!dataset || !algorithm) {
      alert('Please upload a dataset and select an algorithm!');
      return;
    }
    alert(`Running ${algorithm} on dataset: ${dataset.name}`);
    // Logic for sending data to backend can be implemented here
  };

  return (
    <Container>
      {/* Header */}
      <Paper
        style={{
          backgroundColor: '#f5f5f5',

          marginBottom: '20px',
          textAlign: 'center',
          fontFamily: 'Arial, sans-serif',
        }}
      >
        <Typography variant="h4" style={{ color: '#333', fontWeight: 'bold' }}>
          ML Platform
        </Typography>
      </Paper>

      {/* Main Layout */}
      <Grid container spacing={3}>
        {/* Left Pane */}
        <Grid item xs={12} sm={4}>
          <Paper style={{ padding: '20px', height: '100%' }}>
            <Typography variant="h6" style={{ marginBottom: '20px' }}>
              Algorithm Selection
            </Typography>

            {/* Algorithm Selection */}
            <FormControl fullWidth style={{ marginBottom: '20px' }}>
              <InputLabel id="algorithm-select-label">Select Algorithm</InputLabel>
              <Select
                labelId="algorithm-select-label"
                value={algorithm}
                onChange={(e) => setAlgorithm(e.target.value)}
              >
                <MenuItem value="Logistic Regression">Logistic Regression</MenuItem>
                <MenuItem value="Random Forest">Random Forest</MenuItem>
                <MenuItem value="K-Means Clustering">K-Means Clustering</MenuItem>
              </Select>
            </FormControl>

            {/* Run Button */}
            <Button
              variant="contained"
              color="primary"
              onClick={runAlgorithm}
              fullWidth
            >
              Run Algorithm
            </Button>
          </Paper>
        </Grid>

        {/* Right Pane */}
        <Grid item xs={12} sm={8}>
          <Paper style={{ padding: '20px' }}>
            <Typography variant="h6" style={{ marginBottom: '20px' }}>
              Dataset Upload
            </Typography>

            {/* Dataset Upload Section */}
            <div
              {...getRootProps()}
              style={{
                border: '2px dashed #ccc',
                padding: '20px',
                textAlign: 'center',
                cursor: 'pointer',
              }}
            >
              <input {...getInputProps()} />
              <Typography>Drag & drop your dataset file here, or click to select</Typography>
            </div>
            {dataset && (
              <Typography style={{ marginTop: '20px' }}>
                Uploaded File: <strong>{dataset.name}</strong>
              </Typography>
            )}
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
};

export default App;
